import React from 'react'
import { useState } from 'react'
import { Link } from 'react-router-dom'
import './Navigation.css'
import {Navbar,Typography,IconButton,Button,Input,} from "@material-tailwind/react";
// import { BellIcon, Cog6ToothIcon } from "@heroicons/react/24/solid";



const Navigation = () => {

  const poster_url = "https://image.tmdb.org/t/p/w500";

  const [query,setQuery] = useState('')
  const [movie,setMovie] = useState()




const searchMovies = async (e) =>{
  e.preventDefault()

  const searchUrl = `https://api.themoviedb.org/3/search/movie?api_key=27715af1593b0426691060d9f7ad58ee&en-US&query=${query}`

try{
  const response = await fetch(searchUrl)
  const responseJson = await response.json()
  setMovie(responseJson.results)
}catch(err){
 alert("No Find Your Search")
}

}


const inputChange = (e)=>{
  setMovie('')
  setQuery(e.target.value)

}


  return (
    <Navbar
    variant="gradient"
    color="blue-gray"
    className="mx-auto max-w-screen-xl from-blue-gray-900 to-blue-gray-800 px-4 py-3"
  >
    <div className="flex flex-wrap items-center justify-between gap-y-4 text-white">
      <Typography
        as="a"
        href="#"
        variant="h6"
        className="mr-4 ml-2 cursor-pointer py-1.5"
      >
        Material Tailwind
      </Typography>
      <div className="ml-auto flex gap-1 md:mr-4">
        <IconButton variant="text" color="white">
          <Cog6ToothIcon className="h-4 w-4" />
        </IconButton>
        <IconButton variant="text" color="white">
          <BellIcon className="h-4 w-4" />
        </IconButton>
      </div>
      <div className="relative flex w-full gap-2 md:w-max">
        <Input
          type="search"
          color="white"
          label="Type here..."
          className="pr-20"
          containerProps={{
            className: "min-w-[288px]",
          }}
        />
        <Button
          size="sm"
          color="white"
          className="!absolute right-1 top-1 rounded"
        >
          Search
        </Button>
      </div>
    </div>
  </Navbar>
  )
}

export default Navigation



