import React from 'react'
import { useEffect } from 'react';
import { useState } from 'react';

const TvShowRated = () => {

  const poster_url = "https://image.tmdb.org/t/p/w500";

  const [tvRated,setTvRated] = useState()

  const getTvRated = async()=>{
    const url = 'https://api.themoviedb.org/3/tv/top_rated?api_key=27715af1593b0426691060d9f7ad58ee'
    const response = await fetch(url)
    const responseJson = await response.json()

    setTvRated(responseJson.results)
  }

  useEffect(()=>{
    getTvRated()
  },[])


  return (
    <div class="flex flex-wrap justify-center   mx-auto ">
    <div className='grid  lg:grid-cols-3 gap-6 md:grid-cols-2 '>
    {tvRated && tvRated.map((t)=>

<div  className="group relative block bg-black m-10 rounded-xl border">
<img alt={t.title} src={poster_url+t.poster_path}  className="absolute inset-0 h-full w-full object-cover opacity-75 transition-opacity group-hover:opacity-50"/>

<div className="relative p-4 sm:p-6 lg:p-8 mb-20">


<div className=" sm:mt-48 lg:mt-64">
  <div className="translate-y-8 transform opacity-0 transition-all group-hover:translate-y-0 group-hover:opacity-100">
  <p className="text-xl font-bold text-white sm:text-2xl ">{t.title}</p>
    <p className="text-sm text-white"> {t.overview} </p>
  </div>
</div>
</div>
</div>

      )}

      </div>
    </div>
  )
}

export default TvShowRated
